#include <linux/kernel.h>
#include <linux/init.h>
#include <linux/module.h>
#include <crypto/hash.h>

/// str 要加密的字符串
/// len 字符串的长度
/// hash 保存md5的内存大于等于16
/// 成功返回0 否则返回-1
int md5_hash(const char *str, u32 len, u8 *hash)
{
        u32 size=0;
        struct shash_desc *sdescmd5;
        int err = 0;
        struct crypto_shash *md5 = crypto_alloc_shash("md5", 0, 0);
        if (IS_ERR(md5))
                return -1;
        size = sizeof(struct shash_desc) + crypto_shash_descsize(md5);
        sdescmd5 = kmalloc(size, GFP_KERNEL);
        if (!sdescmd5) {
                err = -1;
                goto malloc_err;
        }
        sdescmd5->tfm = md5;
        sdescmd5->flags = 0x0;

        err = crypto_shash_init(sdescmd5);
        if (err) {
                err = -1;
                goto hash_err;
        }
        crypto_shash_update(sdescmd5, str, len);
        err = crypto_shash_final(sdescmd5, hash);

hash_err:
        kfree(sdescmd5);
malloc_err:
        crypto_free_shash(md5);

        return err;
}

static char str[] = "";

#define MD5_FORMAT \
	"%02x%02x%02x%02x%02x%02x%02x%02x%02x%02x%02x%02x%02x%02x%02x%02x"
#define MD5_ARGS(md5_hash) \
	md5_hash[0],  md5_hash[1],  md5_hash[2],  md5_hash[3], \
	md5_hash[4],  md5_hash[5],  md5_hash[6],  md5_hash[7], \
	md5_hash[8],  md5_hash[9],  md5_hash[10], md5_hash[11],\
	md5_hash[12], md5_hash[13], md5_hash[14], md5_hash[15]

char *md5_print(char *out, u8 *in)
{
	snprintf(out, 33, MD5_FORMAT, MD5_ARGS(in));
	return out;
}

int md5_init(void)
{
        u8 md5[16], out[33];
        md5_hash(str, strlen(str), md5);

        md5_print(out, md5);
        pr_info("md5: %s\n", out);

        return 0;
}

void md5_exit(void)
{
        pr_info("exit");
}

module_init(md5_init);
module_exit(md5_exit);
MODULE_LICENSE("GPL");
